const axios = require('axios')

const Index = ({ notes }) => {
  return (
    <div>
      <h3>Welcome to Blog Hub</h3>
      {
        notes.map((note) => {
          return (
            <div key={note._id}>
              <p>{note.title}</p>
            </div>
          )
        })
      }
    </div>
  )
}

Index.getInitialProps = async () => {
  const res = await axios.get('http://localhost:3000/api/notes')

  const { data } = res.json()

  return { notes: data }
}

export default Index
