const axios = require('axios')

const Note = ({ note }) => {
  return (
    <div>
      <h4>{note.title}</h4>
      <p>{note.description}</p>
      <button>Delete</button>
    </div>
  )
}

Note.getInitialProps = async ({ query: { id } }) => {
  const res = await axios.get(`http://localhost:3000/api/notes/${id}`)

  const { data } = res.data
  return { note: data }
}

export default Note
